import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './StudentProfile.css';

const ProfilePage = () => {
  const location = useLocation();
  const { student } = location.state || {};
  const navigate = useNavigate();
  
  const [editedStudent, setEditedStudent] = useState(student || {});

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedStudent((prevStudent) => ({
      ...prevStudent,
      [name]: value,
    }));
  };

  const handleScoreChange = (e, scoreName) => {
    const { value } = e.target;
    setEditedStudent((prevStudent) => ({
      ...prevStudent,
      scores: {
        ...prevStudent.scores,
        [scoreName]: value,
      },
    }));
  };

  const handleSave = () => {
    axios
      .put(`http://localhost:5000/api/data/${student._id}`, editedStudent)
      .then(() => {
        navigate('/app'); // Navigate back to the main app page
      })
      .catch((error) => {
        console.error('Error updating student:', error);
      });
  };

  const handleDelete = () => {
    axios
      .delete(`http://localhost:5000/api/data/${student._id}`)
      .then(() => {
        navigate('/app'); // Navigate back to the main app page
      })
      .catch((error) => {
        console.error('Error deleting student:', error);
      });
  };

  if (!student) {
    return <div>No student selected.</div>;
  }

  return (
    <div className="profile-container">
      <h2>Student Profile</h2>
      <form className="profile-form">
        <label>
          Student ID:
          <input
            type="text"
            name="studentid"
            value={editedStudent.studentid || ''}
            onChange={handleInputChange}
            readOnly
          />
        </label>
        <label>
          Student Name:
          <input
            type="text"
            name="studentname"
            value={editedStudent.studentname || ''}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Year:
          <input
            type="text"
            name="year"
            value={editedStudent.year || ''}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Department:
          <input
            type="text"
            name="department"
            value={editedStudent.department || ''}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Section:
          <input
            type="text"
            name="section"
            value={editedStudent.section || ''}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Gender:
          <input
            type="text"
            name="gender"
            value={editedStudent.gender || ''}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Email ID:
          <input
            type="email"
            name="emailid"
            value={editedStudent.emailid || ''}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Arrear Status:
          <input
            type="text"
            name="arrearstatus"
            value={editedStudent.arrearstatus || ''}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Preferred Degree:
          <input
            type="text"
            name="preferreddegree"
            value={editedStudent.preferreddegree || ''}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Preferred Course:
          <input
            type="text"
            name="preferredcourse"
            value={editedStudent.preferredcourse || ''}
            onChange={handleInputChange}
          />
        </label>
        <label>
          Preferred Country:
          <input
            type="text"
            name="preferredcountry"
            value={editedStudent.preferredcountry || ''}
            onChange={handleInputChange}
          />
        </label>
        {Object.keys(editedStudent.scores || {}).map((scoreName) => (
          <label key={scoreName}>
            {scoreName}:
            <input
              type="text"
              value={editedStudent.scores[scoreName] || ''}
              onChange={(e) => handleScoreChange(e, scoreName)}
            />
          </label>
        ))}
      </form>
      <div className="profile-buttons">
        <button onClick={handleSave}>Save Changes</button>
        <button onClick={handleDelete} className="delete-button">Delete Profile</button>
      </div>
    </div>
  );
};

export default ProfilePage;
