import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './StudentProfile.css';
import image from './Assets/logo.png';

const ProfilePage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { student } = location.state || {};

  const [editedStudent, setEditedStudent] = useState(student || {});

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedStudent((prevStudent) => ({
      ...prevStudent,
      [name]: value,
    }));
  };

  const handleScoreChange = (e, scoreName) => {
    const { value } = e.target;
    setEditedStudent((prevStudent) => ({
      ...prevStudent,
      scores: {
        ...prevStudent.scores,
        [scoreName]: value,
      },
    }));
  };

  const handleSave = () => {
    axios
      .put(`http://localhost:5000/api/data/${student._id}`, editedStudent)
      .then(() => {
        navigate('/app'); // Navigate back to the main app page
      })
      .catch((error) => {
        console.error('Error updating student:', error);
      });
  };

  const handleDelete = () => {
    axios
      .delete(`http://localhost:5000/api/data/${student._id}`)
      .then(() => {
        navigate('/app'); // Navigate back to the main app page
      })
      .catch((error) => {
        console.error('Error deleting student:', error);
      });
  };

  const handleReturn = () => {
    navigate('/app');
  };

  if (!student) {
    return <div>No student data available.</div>;
  }

  return (
    <>
      <div className="header">
        <img src={image} alt="logo" />
      </div>
      <div className="title">
        <h2>Higher Studies Students Data</h2>
      </div>
      <div className="profile-container">
        <div className="profile-header">
          <h1>{student.studentname}</h1>
        </div>
        <form className="profile-details">
          <div className="detail-row">
            <div className="detail-label">RegNo</div>
            <div className="detail-data">
              <input
                type="text"
                name="studentid"
                value={editedStudent.studentid || ''}
                readOnly
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">Department</div>
            <div className="detail-data">
              <input
                type="text"
                name="department"
                value={editedStudent.department || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">Section</div>
            <div className="detail-data">
              <input
                type="text"
                name="section"
                value={editedStudent.section || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">Preferred Course</div>
            <div className="detail-data">
              <input
                type="text"
                name="preferredcourse"
                value={editedStudent.preferredcourse || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">Preferred Country</div>
            <div className="detail-data">
              <input
                type="text"
                name="preferredcountry"
                value={editedStudent.preferredcountry || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">Preferred Degree</div>
            <div className="detail-data">
              <input
                type="text"
                name="preferreddegree"
                value={editedStudent.preferreddegree || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">Email Id</div>
            <div className="detail-data">
              <input
                type="email"
                name="emailid"
                value={editedStudent.emailid || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">CGPA</div>
            <div className="detail-data">
              <input
                type="text"
                name="cgpa"
                value={editedStudent.cgpa || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">ExtraCurricular</div>
            <div className="detail-data">
              <input
                type="text"
                name="extracurricularinfo"
                value={editedStudent.extracurricularinfo || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">Gender</div>
            <div className="detail-data">
              <input
                type="text"
                name="gender"
                value={editedStudent.gender || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">Age</div>
            <div className="detail-data">
              <input
                type="text"
                name="age"
                value={editedStudent.age || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">No Of Projects</div>
            <div className="detail-data">
              <input
                type="text"
                name="numberofprojects"
                value={editedStudent.numberofprojects || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">No Of Researchpapers</div>
            <div className="detail-data">
              <input
                type="text"
                name="numberofresearchpapers"
                value={editedStudent.numberofresearchpapers || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">Level Of Researchpapers</div>
            <div className="detail-data">
              <input
                type="text"
                name="levelofresearchpapers"
                value={editedStudent.levelofresearchpapers || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">Competitions</div>
            <div className="detail-data">
              <input
                type="text"
                name="competitions"
                value={editedStudent.competitions || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">Languages Known</div>
            <div className="detail-data">
              <input
                type="text"
                name="languagesknown"
                value={editedStudent.languagesknown || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">Online Courses</div>
            <div className="detail-data">
              <input
                type="text"
                name="onlinecourses"
                value={editedStudent.onlinecourses || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="detail-row">
            <div className="detail-label">Internship</div>
            <div className="detail-data">
              <input
                type="text"
                name="internship"
                value={editedStudent.internship || ''}
                onChange={handleInputChange}
              />
            </div>
          </div>
          <div className="scores">
            <h3 className="score-heading">Scores</h3>
            {Object.entries(editedStudent.scores || {}).map(([subject, score]) => (
              <div className="detail-row" key={subject}>
                <div className="detail-label">
                  <p>{subject}</p>
                </div>
                <div className="detail-data">
                  <input
                    type="text"
                    value={score}
                    onChange={(e) => handleScoreChange(e, subject)}
                  />
                </div>
              </div>
            ))}
          </div>
        </form>
        <div className="profile-buttons">
          <button onClick={handleSave} className="edit-button">Save Changes</button>
          <button onClick={handleDelete} className="delete-button">Delete Profile</button>
          <button onClick={handleReturn} className="return-button">Return to List</button>
        </div>
      </div>
    </>
  );
};

export default ProfilePage;
