import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './StudentProfile.css';
import image from './Assets/logo.png';

function ProfilePage() {
  const location = useLocation();
  const navigate = useNavigate();
  const { student } = location.state || {};

  const handleReturn = () => {
    navigate('/app');
  };

  const handleDelete = async () => {
    const confirmDelete = window.confirm("Are you sure you want to delete this student?");
    if (confirmDelete) {
      try {
        await fetch(`/api/data/${student._id}`, {
          method: 'DELETE',
        });
        alert('Student deleted successfully.');
        navigate('/app');
      } catch (error) {
        console.error('Error deleting student:', error);
        alert('Failed to delete student.');
      }
    }
  };

  const handleEdit = () => {
    navigate(`/edit/${student._id}`, { state: { student } });
  };

  if (!student) {
    return <div>No student data available.</div>;
  }

  return (
    <>   
      <div className="header">
        <img src={image} alt="logo" />
      </div>
      <div className="title">
        <h2>Higher Studies Students Data</h2>
      </div>
      <div className="profile-container">
        <div className="profile-header">
          <h1>{student.studentname}</h1>
        </div>
        <div className="profile-details">
          {/* All your details rendering code */}
        </div>
        <div className='button'>
          <div>
            <button onClick={handleEdit} className='edit-button'>Edit</button>
          </div>
          <div>
            <button onClick={handleReturn} className="return-button">Return to List</button>
          </div>
          <div>
            <button onClick={handleDelete} className='delete-button'>Delete</button>
          </div>
        </div>
      </div>
    </>
  );
}

export default ProfilePage;
